CREATE PROCEDURE AddNewGroup\n    @PersonIds AS NVARCHAR(MAX)\nAS\nBEGIN\n    DECLARE @NewGroupId INT;\n\n    -- Create a new group\n    INSERT INTO BorrowerGroup DEFAULT VALUES;\n    SET @NewGroupId = SCOPE_IDENTITY();\n\n    -- Insert members into the new group\n    DECLARE @sql NVARCHAR(MAX) = 'INSERT INTO Borrowers (GroupId, PersonId) SELECT ' + \n                                 CAST(@NewGroupId AS NVARCHAR) + ', value FROM STRING_SPLIT(@PersonIds, '','')';\n    EXEC sp_executesql @sql, N'@PersonIds NVARCHAR(MAX)', @PersonIds;\n\n    SELECT @NewGroupId AS GroupId;\nEND;