SELECT \n    dbo.Post.UserId, \n    dbo.Post.Tags, \n    dbo.Post.CommentCount, \n    dbo.Post.Status,  \n    dbo.Post.PostDate, \n    dbo.Post.[Content], \n    dbo.Post.Title, \n    dbo.Post.PostId, \n    dbo.[User].DisplayName \nFROM  \n    dbo.Post \nINNER JOIN\n    dbo.[User] ON dbo.Post.UserId = dbo.[User].UserId \nWHERE \n    PostDate >= DATEADD(mm, DATEDIFF(mm, 0, '2009-01-01'), 0)\n    AND PostDate < DATEADD(mm, DATEDIFF(mm, 0, '2009-02-01'), 0)