SELECT \n    Supplier.SupplierID,\n    COUNT(DISTINCT Computers.ComputerID) AS ComputerCount,\n    COUNT(DISTINCT Cameras.CameraID) AS CameraCount,\n    COUNT(DISTINCT Displays.DisplayID) AS DisplayCount\nFROM \n    Supplier \nLEFT JOIN \n    Computers ON Supplier.SupplierID = Computers.SupplierID\nLEFT JOIN \n    Cameras ON Supplier.SupplierID = Cameras.SupplierID\nLEFT JOIN \n    Displays ON Supplier.SupplierID = Displays.SupplierID\nGROUP BY \n    Supplier.SupplierID