-- Step 1: Drop the foreign key constraint\nALTER TABLE child_table DROP CONSTRAINT fk_child_parent;\n\n-- Step 2: Alter the column size\nALTER TABLE parent_table MODIFY (parent_column VARCHAR2(20));\nALTER TABLE child_table MODIFY (child_column VARCHAR2(20));\n\n-- Step 3: Recreate the foreign key constraint\nALTER TABLE child_table ADD CONSTRAINT fk_child_parent\nFOREIGN KEY (child_column) REFERENCES parent_table(parent_column);