-- Create a temporary table to hold the data\nCREATE TABLE #tbl_Info (Info NVARCHAR(50))\n\n-- Insert sample data into the temporary table\nINSERT INTO #tbl_Info (Info)\nVALUES \n('Name1'), ('Age1'), ('Sex1'),\n('Name2'), ('Age2'), ('Sex2')\n-- Add more rows as needed\n;\n\n-- First CTE to assign row numbers to each group of three rows\nWITH CTE_RowNumber AS (\n    SELECT Info,\n           ROW_NUMBER() OVER (ORDER BY (SELECT NULL)) AS RowNum\n    FROM #tbl_Info\n),\n-- Second CTE to pivot the data\nCTE_Pivot AS (\n    SELECT \n        MAX(CASE WHEN (RowNum - 1) % 3 = 0 THEN Info END) AS Name,\n        MAX(CASE WHEN (RowNum - 1) % 3 = 1 THEN Info END) AS Age,\n        MAX(CASE WHEN (RowNum - 1) % 3 = 2 THEN Info END) AS Sex,\n        (RowNum - 1) / 3 AS GroupNum\n    FROM CTE_RowNumber\n    GROUP BY (RowNum - 1) / 3\n)\n-- Select the final result\nSELECT Name, Age, Sex\nFROM CTE_Pivot\nORDER BY GroupNum;\n\n-- Clean up the temporary table\nDROP TABLE #tbl_Info;