DECLARE\n  p_JOB_ID JOB.JOB_ID%type;\n  outCursor SYS_REFCURSOR;\n  v_JOB JOB%ROWTYPE;\nBEGIN\n  -- Set the input parameter\n  p_JOB_ID := 12345; -- Replace with an actual JOB_ID\n\n  -- Call the stored procedure\n  APP_DB1.GET_JOB(p_JOB_ID, outCursor);\n\n  -- Fetch and display the results\n  LOOP\n    FETCH outCursor INTO v_JOB;\n    EXIT WHEN outCursor%NOTFOUND;\n    -- Display the results\n    DBMS_OUTPUT.PUT_LINE('JOB_ID: ' || v_JOB.JOB_ID || ', JOB_NAME: ' || v_JOB.JOB_NAME);\n  END LOOP;\n\n  -- Close the cursor\n  CLOSE outCursor;\nEND;\n/