CREATE TABLE products (\n    product_id SERIAL PRIMARY KEY,\n    name TEXT NOT NULL,\n    category_id INT REFERENCES categories(category_id),\n    base_price DECIMAL(10, 2) NOT NULL,\n    dynamic_attributes JSONB\n);\n\nCREATE TABLE categories (\n    category_id SERIAL PRIMARY KEY,\n    name TEXT NOT NULL\n);\n\n-- Example of inserting a product with dynamic attributes\nINSERT INTO products (name, category_id, base_price, dynamic_attributes)\nVALUES ('Smart TV', 1, 499.99, '{"tv_resolution": "4K", "screen_size": "55 inches"}');\n\n-- Creating a GIN index on the JSONB column to improve search performance\nCREATE INDEX idx_products_dynamic_attributes ON products USING GIN (dynamic_attributes);\n\n-- Example of querying products with a specific dynamic attribute\nSELECT * FROM products\nWHERE dynamic_attributes @> '{"tv_resolution": "4K"}';\n\n-- Adding a new dynamic attribute at runtime\nUPDATE products\nSET dynamic_attributes = jsonb_set(dynamic_attributes, '{console_depth}', '"10 inches"')\nWHERE product_id = 1;