CREATE PROCEDURE usp_SearchClients\n    @FirstName  VARCHAR(50) = NULL,\n    @LastName   VARCHAR(50) = NULL,\n    @MiddleName VARCHAR(50) = NULL,\n    @DOB        DATETIME = NULL,\n    @HomePhone  VARCHAR(10) = NULL,\n    @WorkPhone  VARCHAR(10) = NULL\nAS\nBEGIN\n    SELECT Last_Name, First_Name, Mid_Name, DOB, Home_Phone, Work_Phone \n    FROM dbo.tblClient\n    WHERE \n        (@LastName IS NULL OR Last_Name LIKE '%' + @LastName + '%') AND\n        (@FirstName IS NULL OR First_Name LIKE '%' + @FirstName + '%') AND\n        (@MiddleName IS NULL OR Mid_Name LIKE '%' + @MiddleName + '%') AND\n        (@DOB IS NULL OR DOB = @DOB) AND\n        (@HomePhone IS NULL OR Home_Phone LIKE '%' + @HomePhone + '%') AND\n        (@WorkPhone IS NULL OR Work_Phone LIKE '%' + @WorkPhone + '%')\nEND