-- Retrieve all communications for workers\nSELECT c.*\nFROM communication c\nJOIN base_actor ba ON c.base_actor_id = ba.id\nJOIN worker w ON ba.id = w.base_actor_id\nWHERE ba.type = 'worker';\n\n-- Retrieve all communications for employers\nSELECT c.*\nFROM communication c\nJOIN base_actor ba ON c.base_actor_id = ba.id\nJOIN employer e ON ba.id = e.base_actor_id\nWHERE ba.type = 'employer';