DECLARE @table TABLE (\n    [column] XML\n)\n\nINSERT @table ([column]) VALUES ('<r><i>1</i><i>2</i><i>3</i></r>')\n\nDECLARE @parameter XML\nSET @parameter = '<r><i>1</i><i>2</i></r>'\n\n-- This query deletes the matching nodes\nUPDATE @table\nSET [column].modify('delete /r/i[text() = sql:variable("@id")]')\nFROM (\n    SELECT T.N.value('.', 'INT') AS id\n    FROM @parameter.nodes('/r/i') AS T(N)\n) AS ParamIds\nWHERE EXISTS (\n    SELECT 1\n    FROM @table T\n    CROSS APPLY T.[column].nodes('/r/i') AS X(N)\n    WHERE X.N.value('.', 'INT') = ParamIds.id\n)\n\n-- To verify the result\nSELECT [column] FROM @table