-- Create indexes on the join and order by columns\nCREATE INDEX idx_stories_categoryid ON Stories (CategoryID);\nCREATE INDEX idx_stories_userid ON Stories (UserID);\nCREATE INDEX idx_stories_lastactivityat ON Stories (LastActivityAt);\nCREATE INDEX idx_categories_id ON Categories (ID);\nCREATE INDEX idx_users_id ON Users (ID);\n\n-- Optimize the query\nSELECT TOP(10) \n    Stories.*, \n    Categories.ShortName, \n    Categories.Name, \n    Users.Username, \n    Users.Email \nFROM Stories\nINNER JOIN Categories ON Categories.ID = Stories.CategoryID\nINNER JOIN Users ON Users.ID = Stories.UserID\nORDER BY Stories.LastActivityAt DESC;