-- Adding indexes\nCREATE INDEX idx_topic_id ON wp_pod_tbl_forum(topic_id);\nCREATE INDEX idx_date ON wp_pod_tbl_forum(date);\n\n-- Optimized query\nSELECT SQL_CALC_FOUND_ROWS\n    t.id, \n    t.name, \n    COALESCE(MAX(r.date), t.date) AS date, \n    COUNT(r.id) AS replies\nFROM\n    wp_pod_tbl_forum t\nLEFT JOIN \n    (SELECT topic_id, MAX(date) AS date, COUNT(id) AS replies \n     FROM wp_pod_tbl_forum \n     WHERE topic_id != 0 \n     GROUP BY topic_id) r \nON t.id = r.topic_id\nWHERE\n    t.topic_id = 0\nGROUP BY\n    t.id, t.name, t.date\nORDER BY\n    date DESC \nLIMIT 0, 20;