CREATE TRIGGER update_vote_summary AFTER INSERT ON votes\n   FOR EACH ROW\n   BEGIN\n       IF NEW.vote_value = 'Yes' THEN\n           UPDATE vote_summaries SET yes_score = yes_score + 2 WHERE question_id = NEW.question_id;\n       ELSEIF NEW.vote_value = 'No' THEN\n           UPDATE vote_summaries SET no_score = no_score + 0 WHERE question_id = NEW.question_id;\n       ELSEIF NEW.vote_value = 'Maybe' THEN\n           UPDATE vote_summaries SET maybe_score = maybe_score + 1 WHERE question_id = NEW.question_id;\n       END IF;\n   END;