CREATE TEMPORARY TABLE week_dates AS\nSELECT CURDATE() - INTERVAL (WEEKDAY(CURDATE())) DAY + INTERVAL seq DAY AS date\nFROM (SELECT 0 seq UNION ALL SELECT 1 UNION ALL SELECT 2 UNION ALL SELECT 3 UNION ALL SELECT 4 UNION ALL SELECT 5 UNION ALL SELECT 6) seq_table\nWHERE CURDATE() - INTERVAL (WEEKDAY(CURDATE())) DAY + INTERVAL seq DAY BETWEEN CURDATE() - INTERVAL (WEEKDAY(CURDATE())) DAY AND CURDATE() + INTERVAL (6 - WEEKDAY(CURDATE())) DAY;