SELECT \n    d.DataId, \n    (SELECT [Name] FROM Category WHERE CategoryId = d.CategoryId) AS 'Category', \n    d.Description, \n    d.CompanyName, \n    d.City, \n    d.CategoryId,\n    d.CreatedOn, \n    d.Rank, \n    d.voteCount, \n    d.commentCount, \n    d.viewCount,\n    kts.RANK\nFROM \n    Data d \n    INNER JOIN Keyword k ON d.DataId = k.DataId\n    INNER JOIN CONTAINSTABLE(Keyword, Keyword, @SearchQ) AS kts ON kts.[KEY] = k.DataId\nWHERE \n    d.CategoryId = @CategoryId \n    AND d.IsSearch = 1 \n    AND d.IsApproved = 1\nORDER BY \n    kts.RANK DESC;