-- Example to paginate the results, showing 20 images per page\n-- Replace {PAGE_NUMBER} with the current page number\n-- and {IMAGES_PER_PAGE} with the number of images per page (e.g., 20)\n\nSET @PageNumber = {PAGE_NUMBER};\nSET @ImagesPerPage = {IMAGES_PER_PAGE};\nSET @Offset = (@PageNumber - 1) * @ImagesPerPage;\n\nSELECT id, foto_albums_id, user_id, title\nFROM (\n    SELECT \n        id, \n        foto_albums_id, \n        user_id, \n        title, \n        ROW_NUMBER() OVER (PARTITION BY user_id ORDER BY id DESC) as row_num\n    FROM \n        foto_images\n) AS subquery\nWHERE \n    row_num <= 5\nORDER BY \n    id DESC\nLIMIT @ImagesPerPage OFFSET @Offset;