-- This query limits the number of images per user to 5 to ensure a balanced display\nSELECT id, foto_albums_id, user_id, title\nFROM (\n    SELECT \n        id, \n        foto_albums_id, \n        user_id, \n        title, \n        ROW_NUMBER() OVER (PARTITION BY user_id ORDER BY id DESC) as row_num\n    FROM \n        foto_images\n) AS subquery\nWHERE \n    row_num <= 5\nORDER BY \n    id DESC;