CREATE PROCEDURE EncryptData\n   @plaintext NVARCHAR(MAX)\n   AS\n   BEGIN\n       OPEN SYMMETRIC KEY SymmetricKeyName\n       DECRYPTION BY PASSWORD = 'YourStrongPassword';\n\n       DECLARE @ciphertext VARBINARY(MAX);\n       SET @ciphertext = EncryptByKey(Key_GUID('SymmetricKeyName'), @plaintext);\n\n       CLOSE SYMMETRIC KEY SymmetricKeyName;\n\n       SELECT @ciphertext AS EncryptedData;\n   END