-- Users Table\nCREATE TABLE users (\n    id INT PRIMARY KEY,\n    name VARCHAR(255),\n    surname VARCHAR(255),\n    nickname VARCHAR(255),\n    email VARCHAR(255),\n    phone VARCHAR(50)\n);\n\n-- Attributes Table\nCREATE TABLE attributes (\n    id INT PRIMARY KEY,\n    name VARCHAR(255),\n    type VARCHAR(50) -- Example: 'email', 'phone', 'url', etc.\n);\n\n-- UserAttributes Table\nCREATE TABLE user_attributes (\n    id INT PRIMARY KEY,\n    user_id INT,\n    attribute_id INT,\n    value VARCHAR(255),\n    FOREIGN KEY (user_id) REFERENCES users(id) ON DELETE CASCADE,\n    FOREIGN KEY (attribute_id) REFERENCES attributes(id) ON DELETE CASCADE\n);