CREATE PROCEDURE DecryptData\n   @ciphertext VARBINARY(MAX)\n   AS\n   BEGIN\n       OPEN SYMMETRIC KEY SymmetricKeyName\n       DECRYPTION BY PASSWORD = 'YourStrongPassword';\n\n       DECLARE @plaintext NVARCHAR(MAX);\n       SET @plaintext = CONVERT(NVARCHAR(MAX), DecryptByKey(@ciphertext));\n\n       CLOSE SYMMETRIC KEY SymmetricKeyName;\n\n       SELECT @plaintext AS DecryptedData;\n   END