CREATE PROCEDURE UpdateTables\nAS\nBEGIN\n    -- SET NOCOUNT ON added to prevent extra result sets from\n    -- interfering with SELECT statements.\n    SET NOCOUNT ON;\n\n    DECLARE @Table1Rows INT, @Table2Rows INT, @Table3Rows INT, @Table4Rows INT;\n\n    UPDATE Table1 SET Column = 0 WHERE Column IS NULL;\n    SET @Table1Rows = @@ROWCOUNT;\n\n    UPDATE Table2 SET Column = 0 WHERE Column IS NULL;\n    SET @Table2Rows = @@ROWCOUNT;\n\n    UPDATE Table3 SET Column = 0 WHERE Column IS NULL;\n    SET @Table3Rows = @@ROWCOUNT;\n\n    UPDATE Table4 SET Column = 0 WHERE Column IS NULL;\n    SET @Table4Rows = @@ROWCOUNT;\n\n    SELECT @Table1Rows AS Table1, @Table2Rows AS Table2, @Table3Rows AS Table3, @Table4Rows AS Table4;\nEND