-- Insert into parent table\nINSERT INTO array_parent (array_id, description) VALUES (1, 'Example Array');\n\n-- Insert elements into child table\nBEGIN\n    FOR i IN 1..10000 LOOP\n        INSERT INTO array_element (element_id, array_id, element_index, element_value)\n        VALUES (i, 1, i, 'Value ' || i);\n    END LOOP;\nEND;\n/\n\n-- Retrieve an element\nSELECT get_array_element(1, 500) FROM dual;