-- Trigger for INSERT on table1\nCREATE TRIGGER after_insert_table1\nAFTER INSERT ON table1\nFOR EACH ROW\nBEGIN\n    INSERT INTO materialized_view (column1, column2, ...)\n    SELECT NEW.column1, t2.column2, ...\n    FROM table2 t2\n    WHERE t2.id = NEW.id;\nEND;\n\n-- Trigger for UPDATE on table1\nCREATE TRIGGER after_update_table1\nAFTER UPDATE ON table1\nFOR EACH ROW\nBEGIN\n    UPDATE materialized_view\n    SET column1 = NEW.column1\n    WHERE id = NEW.id;\nEND;\n\n-- Trigger for DELETE on table1\nCREATE TRIGGER after_delete_table1\nAFTER DELETE ON table1\nFOR EACH ROW\nBEGIN\n    DELETE FROM materialized_view WHERE id = OLD.id;\nEND;