DECLARE @Column1 INT, @Column2 NVARCHAR(100)\n\n-- Declare cursor\nDECLARE MyCursor CURSOR FOR\nSELECT Column1, Column2\nFROM YourTable\nWHERE SomeCriteria = 'SomeValue'\n\n-- Open cursor\nOPEN MyCursor\n\n-- Fetch the first row\nFETCH NEXT FROM MyCursor INTO @Column1, @Column2\n\n-- Loop through all fetched rows\nWHILE @@FETCH_STATUS = 0\nBEGIN\n    -- Perform your row-by-row operations here\n    EXEC dbo.YourStoredProc @Column1, @Column2\n    -- Or other T-SQL code to send an email, run a job, etc.\n\n    -- Fetch the next row\n    FETCH NEXT FROM MyCursor INTO @Column1, @Column2\nEND\n\n-- Close and deallocate cursor\nCLOSE MyCursor\nDEALLOCATE MyCursor