DECLARE @myDoc xml\nDECLARE @WarrantyText nvarchar(100)\n\nSET @myDoc = '<Root>\n<ProductDescription ProductID="1" ProductName="Road Bike">\n<Features>\n  <Warranty>1 year parts and labor</Warranty>\n  <Maintenance>3 year parts and labor extended maintenance is available</Maintenance>\n</Features>\n</ProductDescription>\n</Root>'\n\nSET @WarrantyText = @myDoc.value('(/Root/ProductDescription/Features/Warranty/text())[1]', 'nvarchar(100)')\nSELECT @WarrantyText