SELECT \n    LEFT(blockid, 6) AS cadastralMapNo, \n    COUNT(DISTINCT RIGHT(blockid, 2)) AS noofBlocks,\n    SUM(CASE WHEN stateorprivate = 'Deemana' THEN 1 ELSE 0 END) AS Deemana,\n    SUM(CASE WHEN stateorprivate = 'State' THEN 1 ELSE 0 END) AS State,\n    SUM(CASE WHEN stateorprivate = 'Private' THEN 1 ELSE 0 END) AS Private,\n    SUM(CASE WHEN stateorprivate IN ('Deemana', 'State', 'Private') THEN 1 ELSE 0 END) AS Amt_of_Deemana_State_Private\nFROM \n    LandParcels\nGROUP BY \n    LEFT(blockid, 6)\nORDER BY \n    LEFT(blockid, 6) ASC;