-- Creating a sample table\nCREATE TABLE example (\n    id INT,\n    name VARCHAR(50)\n);\n\n-- Inserting sample data\nINSERT INTO example (id, name) VALUES\n(1, 'Alice'),\n(2, NULL),\n(3, 'Bob'),\n(4, NULL);\n\n-- Using COUNT(*)\nSELECT COUNT(*) FROM example;  -- This will return 4\n\n-- Using COUNT(name)\nSELECT COUNT(name) FROM example;  -- This will return 2 because only two rows have a non-NULL value for 'name'