SELECT movie.title, lead_actor.name \nFROM movie\nJOIN casting julie_casting\n    ON movie.id = julie_casting.movieid\nJOIN actor julie_actor\n    ON julie_casting.actorid = julie_actor.id\nJOIN casting lead_casting\n    ON movie.id = lead_casting.movieid AND lead_casting.ord = 1\nJOIN actor lead_actor\n    ON lead_casting.actorid = lead_actor.id\nWHERE julie_actor.name = 'Julie Andrews';