-- Create an empty database and replicate the schema\n   CREATE DATABASE [NewDatabase]\n   GO\n\n   USE [NewDatabase]\n   GO\n\n   -- Script to copy schema\n   DECLARE @sqlCmd VARCHAR(MAX)\n   SET @sqlCmd = 'sqlcmd -S YourServerName -d YourDatabase -Q "SELECT * INTO [NewDatabase].[dbo].[YourTable] FROM [YourDatabase].[dbo].[YourTable] WHERE 1=0"'\n   EXEC xp_cmdshell @sqlCmd\n\n   -- Import data from CSV file\n   SET @sqlCmd = 'bcp [NewDatabase].[dbo].[YourTable] in "C:\Backup\YourTable_Owner1.csv" -c -t, -T -S YourServerName'\n   EXEC xp_cmdshell @sqlCmd