CREATE TRIGGER trg_delete_master_record\nAFTER DELETE ON MasterRecordTable\nFOR EACH ROW\nBEGIN\n    DELETE FROM Table1 WHERE MasterRecordID = OLD.id;\n    DELETE FROM Table2 WHERE MasterRecordID = OLD.id;\n\n    DELETE FROM Table3 WHERE Table1ID IN (SELECT table1ID FROM Table1 WHERE MasterRecordID = OLD.id);\n    DELETE FROM Table4 WHERE Table1ID IN (SELECT table1ID FROM Table1 WHERE MasterRecordID = OLD.id);\n    DELETE FROM Table5 WHERE Table2ID IN (SELECT Table2ID FROM Table2 WHERE MasterRecordID = OLD.id);\nEND;