-- Ensure indexes on both tables\nCREATE INDEX idx_auditdata_id ON auditdata (ID);\nCREATE INDEX idx_auditdata_sms_12_id ON Auditdata_sms_12 (id);\n\n-- Batch update example\nDECLARE @BatchSize INT = 1000;\nDECLARE @RowCount INT;\n\nSET @RowCount = 1;\n\nWHILE @RowCount > 0\nBEGIN\n    UPDATE TOP (@BatchSize) auditdata\n    SET TATCallType = '12', TATCallUnit = '1'\n    FROM auditdata\n    INNER JOIN Auditdata_sms_12 a_sns\n    ON auditdata.ID = a_sns.id;\n\n    SET @RowCount = @@ROWCOUNT;\nEND\n\n-- Update statistics\nUPDATE STATISTICS auditdata;\nUPDATE STATISTICS Auditdata_sms_12;