-- Step 1: Create a temporary table to store the sample data\nCREATE TEMPORARY TABLE SampleData (\n    Table_Name VARCHAR(255),\n    Data_Type VARCHAR(255),\n    Sample_Data VARCHAR(255)\n);\n\n-- Step 2: Insert data into the temporary table\nINSERT INTO SampleData (Table_Name, Data_Type, Sample_Data)\nSELECT \n    col.TABLE_NAME,\n    col.DATA_TYPE,\n    (SELECT TOP 1 col.COLUMN_NAME FROM col.TABLE_NAME) AS Sample_Data\nFROM \n    INFORMATION_SCHEMA.COLUMNS col\nORDER BY \n    col.TABLE_CATALOG, col.TABLE_NAME, col.TABLE_SCHEMA, col.ORDINAL_POSITION;\n\n-- Step 3: Select from the temporary table to view the pivoted data\nSELECT \n    Table_Name,\n    Data_Type,\n    Sample_Data\nFROM \n    SampleData\nORDER BY \n    Table_Name;\n\n-- Clean up: Drop the temporary table\nDROP TABLE SampleData;