CREATE TRIGGER trg_after_update\nON YourTable\nFOR UPDATE\nAS\nBEGIN\n    DECLARE @updatedColumns VARBINARY(150);\n    SET @updatedColumns = COLUMNS_UPDATED();\n\n    -- Check if only one specific column (e.g., Column1) is updated\n    IF (UPDATE(Column1) AND @updatedColumns = 1)\n    BEGIN\n        -- Your logic here when only Column1 is updated\n        PRINT 'Only Column1 was updated';\n    END\n    ELSE\n    BEGIN\n        -- Your logic here when other columns or multiple columns are updated\n        PRINT 'Other columns or multiple columns were updated';\n    END\nEND;\nGO