CREATE OR REPLACE PROCEDURE GetSortedAndFilteredData (\n    p_sort_clause   IN VARCHAR2,\n    p_filter_clause IN VARCHAR2,\n    p_cursor        OUT SYS_REFCURSOR\n) AS\n    l_sql  VARCHAR2(4000);\nBEGIN\n    l_sql := 'SELECT * FROM YourTable';\n    \n    IF p_filter_clause IS NOT NULL THEN\n        l_sql := l_sql || ' WHERE ' || p_filter_clause;\n    END IF;\n    \n    IF p_sort_clause IS NOT NULL THEN\n        l_sql := l_sql || ' ORDER BY ' || p_sort_clause;\n    END IF;\n    \n    OPEN p_cursor FOR l_sql;\nEND;\n/