CREATE TABLE row_count (\n    table_name VARCHAR(255) PRIMARY KEY,\n    count BIGINT NOT NULL\n);\n\n-- Initialize the row count (example for 'my_table')\nINSERT INTO row_count (table_name, count)\nSELECT 'my_table', COUNT(*) FROM my_table;\n\n-- Trigger function to update count on insert\nCREATE OR REPLACE FUNCTION increment_count()\nRETURNS TRIGGER AS $$\nBEGIN\n    UPDATE row_count\n    SET count = count + 1\n    WHERE table_name = TG_TABLE_NAME;\n    RETURN NEW;\nEND;\n$$ LANGUAGE plpgsql;\n\n-- Trigger function to update count on delete\nCREATE OR REPLACE FUNCTION decrement_count()\nRETURNS TRIGGER AS $$\nBEGIN\n    UPDATE row_count\n    SET count = count - 1\n    WHERE table_name = TG_TABLE_NAME;\n    RETURN OLD;\nEND;\n$$ LANGUAGE plpgsql;\n\n-- Attach triggers to the table (example for 'my_table')\nCREATE TRIGGER my_table_insert_trigger\nAFTER INSERT ON my_table\nFOR EACH ROW\nEXECUTE FUNCTION increment_count();\n\nCREATE TRIGGER my_table_delete_trigger\nAFTER DELETE ON my_table\nFOR EACH ROW\nEXECUTE FUNCTION decrement_count();