BEGIN TRANSACTION;\n\n-- Drop new constraints\nIF EXISTS (SELECT * FROM sys.objects WHERE object_id = OBJECT_ID(N'[dbo].[NewConstraint]') AND type = 'D')\nBEGIN\n    ALTER TABLE [dbo].[YourTable] DROP CONSTRAINT [NewConstraint];\nEND\n\n-- Drop new indexes\nIF EXISTS (SELECT * FROM sys.indexes WHERE name = N'NewIndex' AND object_id = OBJECT_ID(N'[dbo].[YourTable]'))\nBEGIN\n    DROP INDEX [NewIndex] ON [dbo].[YourTable];\nEND\n\n-- Remove new columns\nIF EXISTS (SELECT * FROM sys.columns WHERE Name = N'NewColumn' AND Object_ID = Object_ID(N'[dbo].[YourTable]'))\nBEGIN\n    ALTER TABLE [dbo].[YourTable] DROP COLUMN [NewColumn];\nEND\n\n-- Drop added tables\nIF OBJECT_ID(N'[dbo].[NewTable]', 'U') IS NOT NULL\nBEGIN\n    DROP TABLE [dbo].[NewTable];\nEND\n\nCOMMIT TRANSACTION;