-- Full backup once a day\nBACKUP DATABASE [YourDatabase] TO DISK = N'\\NetworkPath\YourDatabase_full.bak' \nWITH INIT, NAME = N'YourDatabase-Full Database Backup', \nSKIP, NOREWIND, NOUNLOAD, STATS = 10;\n\n-- Differential backup every 4 hours\nBACKUP DATABASE [YourDatabase] TO DISK = N'\\NetworkPath\YourDatabase_diff.bak' \nWITH DIFFERENTIAL, INIT, NAME = N'YourDatabase-Differential Database Backup', \nSKIP, NOREWIND, NOUNLOAD, STATS = 10;\n\n-- Transaction log backup every 15 minutes\nBACKUP LOG [YourDatabase] TO DISK = N'\\NetworkPath\YourDatabase_log.trn' \nWITH INIT, NAME = N'YourDatabase-Transaction Log Backup', \nSKIP, NOREWIND, NOUNLOAD, STATS = 10;