-- Create Full Text Catalog\nCREATE FULLTEXT CATALOG MyCatalog\n\n-- Create Full Text Index on the table and column\nCREATE FULLTEXT INDEX ON MyTable(MyColumn)\nKEY INDEX PK_MyTable -- Assume PK_MyTable is the primary key index name\nON MyCatalog\n\n-- Populate Full Text Index\nEXEC sp_fulltext_catalog 'MyCatalog', 'start_full'\n\n-- Perform a Full Text Search query\nSELECT *\nFROM MyTable\nWHERE CONTAINS(MyColumn, 'search term')