SELECT number, COUNT(*) as frequency\nFROM (\n    SELECT n1 as number FROM lottery\n    UNION ALL\n    SELECT n2 as number FROM lottery\n    UNION ALL\n    SELECT n3 as number FROM lottery\n    UNION ALL\n    SELECT n4 as number FROM lottery\n    UNION ALL\n    SELECT n5 as number FROM lottery\n    UNION ALL\n    SELECT n6 as number FROM lottery\n) as combined\nGROUP BY number\nORDER BY frequency DESC;