-- Find accounts added\nINSERT INTO AccountsAdded (AccountNum, Name)\nSELECT n.AccountNum, n.Name\nFROM AccountsNew n\nLEFT JOIN AccountsCurrent c ON n.AccountNum = c.AccountNum\nWHERE c.AccountNum IS NULL;\n\n-- Find accounts removed\nINSERT INTO AccountsRemoved (AccountNum, Name)\nSELECT c.AccountNum, c.Name\nFROM AccountsCurrent c\nLEFT JOIN AccountsNew n ON n.AccountNum = c.AccountNum\nWHERE n.AccountNum IS NULL;\n\n-- Update AccountsCurrent\nTRUNCATE TABLE AccountsCurrent;\n\nINSERT INTO AccountsCurrent (AccountNum, Name)\nSELECT AccountNum, Name FROM AccountsNew;\n\n-- Clear AccountsNew\nTRUNCATE TABLE AccountsNew;