-- Calculate average volume of sale\nWITH SalesVolume AS (\n    SELECT e.EmployeeID, SUM(o.Quantity * p.Price) AS TotalSales\n    FROM Employees e\n    JOIN Orders o ON e.EmployeeID = o.EmployeeID\n    JOIN Products p ON o.ProductID = p.ProductID\n    GROUP BY e.EmployeeID\n),\nAverageSales AS (\n    SELECT AVG(TotalSales) AS AvgSales\n    FROM SalesVolume\n)\nSELECT e.EmployeeID, e.Name\nFROM Employees e\nJOIN SalesVolume sv ON e.EmployeeID = sv.EmployeeID\nJOIN AverageSales avs\nWHERE sv.TotalSales < avs.AvgSales;