CREATE TABLE tblImages (\n    imageID INT UNSIGNED NOT NULL AUTO_INCREMENT,\n    PRIMARY KEY (imageID)\n    -- other columns\n);\n\nCREATE TABLE tblImageFlags (\n    imageFlagID INT UNSIGNED NOT NULL AUTO_INCREMENT,\n    imageID INT UNSIGNED NOT NULL,\n    flagTypeID INT UNSIGNED NOT NULL,\n    resolutionTypeID INT UNSIGNED,\n    PRIMARY KEY (imageFlagID),\n    FOREIGN KEY (imageID) REFERENCES tblImages(imageID),\n    FOREIGN KEY (flagTypeID) REFERENCES luFlagTypes(flagTypeID),\n    FOREIGN KEY (resolutionTypeID) REFERENCES luResolutionTypes(resolutionTypeID)\n    -- other columns\n);\n\nCREATE TABLE luResolutionTypes (\n    resolutionTypeID INT UNSIGNED NOT NULL AUTO_INCREMENT,\n    resolutionType VARCHAR(63) NOT NULL,\n    PRIMARY KEY (resolutionTypeID)\n    -- other columns\n);\n\nCREATE TABLE luFlagTypes (\n    flagTypeID INT UNSIGNED NOT NULL AUTO_INCREMENT,\n    flagType VARCHAR(63) NOT NULL,\n    PRIMARY KEY (flagTypeID)\n    -- other columns\n);