-- Main sample table\nCREATE TABLE SAMPLE (\n    sample_id INT PRIMARY KEY,\n    sample_template_id INT,\n    -- other common fields\n);\n\n-- Template table to define different types of samples\nCREATE TABLE SAMPLE_TEMPLATE (\n    sample_template_id INT PRIMARY KEY,\n    version INT,\n    status VARCHAR(50),\n    date_created DATE,\n    name VARCHAR(100)\n);\n\n-- Mapping table to associate attributes with templates\nCREATE TABLE SAMPLE_ATTR_OF (\n    sample_template_id INT,\n    sample_attribute_id INT,\n    PRIMARY KEY (sample_template_id, sample_attribute_id),\n    FOREIGN KEY (sample_template_id) REFERENCES SAMPLE_TEMPLATE(sample_template_id),\n    FOREIGN KEY (sample_attribute_id) REFERENCES SAMPLE_ATTRIBUTE(sample_attribute_id)\n);\n\n-- Attribute metadata table\nCREATE TABLE SAMPLE_ATTRIBUTE (\n    sample_attribute_id INT PRIMARY KEY,\n    name VARCHAR(100),\n    description VARCHAR(255)\n);\n\n-- Table to store numeric values of attributes\nCREATE TABLE SAMPLE_NUMBER (\n    sample_id INT,\n    sample_attribute_id INT,\n    value NUMERIC,\n    PRIMARY KEY (sample_id, sample_attribute_id),\n    FOREIGN KEY (sample_id) REFERENCES SAMPLE(sample_id),\n    FOREIGN KEY (sample_attribute_id) REFERENCES SAMPLE_ATTRIBUTE(sample_attribute_id)\n);\n\n-- Table to store date values of attributes\nCREATE TABLE SAMPLE_DATE (\n    sample_id INT,\n    sample_attribute_id INT,\n    value DATE,\n    PRIMARY KEY (sample_id, sample_attribute_id),\n    FOREIGN KEY (sample_id) REFERENCES SAMPLE(sample_id),\n    FOREIGN KEY (sample_attribute_id) REFERENCES SAMPLE_ATTRIBUTE(sample_attribute_id)\n);\n\n-- Similarly, you can create tables for other data types like SAMPLE_STRING, SAMPLE_BOOLEAN, etc.