-- Enable TDE on the database\nUSE master;\nGO\n\n-- Create a master key if it does not already exist\nCREATE MASTER KEY ENCRYPTION BY PASSWORD = 'YourStrongPassword';\nGO\n\n-- Create a certificate protected by the master key\nCREATE CERTIFICATE MyServerCert WITH SUBJECT = 'My Database Encryption Certificate';\nGO\n\n-- Create a database encryption key and protect it by the certificate\nUSE YourDatabaseName;\nGO\nCREATE DATABASE ENCRYPTION KEY\nWITH ALGORITHM = AES_256\nENCRYPTION BY SERVER CERTIFICATE MyServerCert;\nGO\n\n-- Enable encryption on the database\nALTER DATABASE YourDatabaseName\nSET ENCRYPTION ON;\nGO