DECLARE @next INT = 0;\nDECLARE @owner NVARCHAR(128);\nDECLARE @table NVARCHAR(128);\nDECLARE @fullTableName NVARCHAR(256);\nDECLARE @sql NVARCHAR(MAX);\n\nWHILE 1=1\nBEGIN\n    -- Get the next table that contains the 'criteria1' column\n    SELECT TOP 1 @next = o.id,\n                 @table = o.name,\n                 @owner = u.name\n    FROM syscolumns c\n    INNER JOIN sysobjects o ON c.id = o.id\n    LEFT JOIN sysusers u ON o.uid = u.uid\n    WHERE c.name = 'criteria1' AND o.id > @next\n    ORDER BY o.id;\n\n    -- Break the loop if no more tables are found\n    IF @next IS NULL\n        BREAK;\n\n    -- Construct the full table name\n    SET @fullTableName = QUOTENAME(@owner) + '.' + QUOTENAME(@table);\n\n    -- Construct the dynamic SQL query\n    SET @sql = 'SELECT criteria1 FROM ' + @fullTableName + ' WHERE linenum = 1;';\n\n    -- Execute the dynamic SQL query\n    EXEC sp_executesql @sql;\n    \nEND;