SELECT\n    creator.id AS creator_id,\n    updater.id AS updater_id,\n    owner.id AS owner_id,\n    asset.[name]\nFROM\n    asset\nLEFT JOIN\n    [user] AS creator ON creator.user_pk = asset.created_by\nLEFT JOIN\n    [user] AS updater ON updater.user_pk = asset.updated_by\nLEFT JOIN\n    [user] AS owner ON owner.user_pk = asset.owned_by\nWHERE\n    creator.active = 1 AND\n    updater.active = 1 AND\n    owner.active = 1;