-- Create an audit table\nCREATE TABLE your_table_audit (\n    audit_id NUMBER PRIMARY KEY,\n    action VARCHAR2(10),\n    table_name VARCHAR2(30),\n    old_row your_table%ROWTYPE,\n    new_row your_table%ROWTYPE,\n    change_timestamp TIMESTAMP\n);\n\n-- Create a trigger to log changes\nCREATE OR REPLACE TRIGGER your_table_audit_trg\nAFTER INSERT OR UPDATE OR DELETE ON your_table\nFOR EACH ROW\nBEGIN\n    IF INSERTING THEN\n        INSERT INTO your_table_audit (audit_id, action, table_name, new_row, change_timestamp)\n        VALUES (your_table_audit_seq.NEXTVAL, 'INSERT', 'your_table', :NEW, SYSTIMESTAMP);\n    ELSIF UPDATING THEN\n        INSERT INTO your_table_audit (audit_id, action, table_name, old_row, new_row, change_timestamp)\n        VALUES (your_table_audit_seq.NEXTVAL, 'UPDATE', 'your_table', :OLD, :NEW, SYSTIMESTAMP);\n    ELSIF DELETING THEN\n        INSERT INTO your_table_audit (audit_id, action, table_name, old_row, change_timestamp)\n        VALUES (your_table_audit_seq.NEXTVAL, 'DELETE', 'your_table', :OLD, SYSTIMESTAMP);\n    END IF;\nEND;\n/