-- Define the size of each chunk\nDECLARE @ChunkSize INT = 1000; -- Set the appropriate chunk size\n\n-- Assume you have a table named `YourTable` with a `VARBINARY` column `YourVarbinaryColumn`\n-- and a unique identifier column `ID`\n\n-- Query to split the VARBINARY data into chunks\nWITH BinaryChunks AS (\n    SELECT\n        ID,\n        SUBSTRING(YourVarbinaryColumn, chunk * @ChunkSize + 1, @ChunkSize) AS ChunkData,\n        chunk\n    FROM\n        YourTable\n    CROSS APPLY(\n        SELECT TOP (CEILING(DATALENGTH(YourVarbinaryColumn) * 1.0 / @ChunkSize))\n            ROW_NUMBER() OVER (ORDER BY (SELECT NULL)) - 1 AS chunk\n        FROM master.dbo.spt_values\n    ) AS ChunkNumbers\n)\nSELECT\n    ID,\n    ChunkData,\n    chunk\nFROM\n    BinaryChunks\nORDER BY\n    ID,\n    chunk;