SELECT \n    YEAR(deliveryDate) AS Year,\n    MONTH(deliveryDate) AS Month,\n    DAY(deliveryDate) AS Day,\n    DATEPART(week, deliveryDate) AS Week,\n    DATEPART(hour, deliveryDate) AS Hour,\n    SUM(someValues) AS TotalValues\nFROM table1\nGROUP BY \n    YEAR(deliveryDate),\n    MONTH(deliveryDate),\n    DAY(deliveryDate),\n    DATEPART(week, deliveryDate),\n    DATEPART(hour, deliveryDate)\nORDER BY \n    Year, Month, Day, Week, Hour;