CREATE OR REPLACE PACKAGE BODY test_set_shift_times_to_null IS\n\nPROCEDURE test_set_shift_times_to_null_basic IS\n   l_retval            VARCHAR2(100);\n   l_errbuf            VARCHAR2(100);\n   l_records_updated   NUMBER;\nBEGIN\n   -- Setup: Insert test data into employees and night_shift tables\n   INSERT INTO employees (employee_id, employee_name, shift)\n   VALUES (1, 'John Doe', 'EVENING');\n   \n   INSERT INTO employees (employee_id, employee_name, shift)\n   VALUES (2, 'Jane Smith', 'DAY');\n   \n   INSERT INTO night_shift (record_id, some_dumb_field)\n   VALUES (1, NULL);\n   \n   COMMIT;\n\n   -- Call the procedure\n   my_package.set_shift_times_to_null(l_retval, l_errbuf, l_records_updated);\n\n   -- Check records_updated count\n   ut.expect(l_records_updated).to_equal(1);\n\n   -- Check the retval value\n   ut.expect(l_retval).to_equal('0');\n\n   -- Validate the updates in night_shift table\n   DECLARE\n      l_value VARCHAR2(100);\n   BEGIN\n      SELECT some_dumb_field\n      INTO l_value\n      FROM night_shift\n      WHERE record_id = 1;\n\n      ut.expect(l_value).to_equal('John Doe');\n   END;\n\nEXCEPTION\n   WHEN OTHERS THEN\n      ROLLBACK;\n      RAISE;\nEND test_set_shift_times_to_null_basic;\n\nEND test_set_shift_times_to_null;\n/